import functools

class ParameterizedProxy(object):
    """
    A way to pass some params to a proxy
    """
    
    def __init__(self, proxy, params):
        
        self.proxy = proxy
        self.params = params
        
        
    def __getattr__(self, name):
        
        return functools.partial( getattr( self.proxy, name ), params = self.params )
    
    
    @property
    def NAME(self):
        
        return self.proxy.NAME
