select cluster90_id, conc_comma(pdbid), count(*) from (select * from (select pdbfileid,pdbid from pdbfiles where resolution<1.5) a join (select distinct pdbfileid,organism_id from components where organism_id in (9606,10090,10116,9913)) b on a.pdbfileid=b.pdbfileid join (select distinct pdbfileid, cluster90_id from ccdinfo) c on a.pdbfileid=c.pdbfileid join (select distinct pdbfileid from neighbors1515_cys_cys where atomneighbortype=1111) d on a.pdbfileid=d.pdbfileid) d group by cluster90_id order by count desc;
